def Total_Hamming_Distance(n):
    # Calculate sum of Hamming distances between all consecutive numbers from 0 to n
    total = 0
    for bits in range(32):
        # Calculate the number of 0s and 1s in this bit position for all numbers
        ones = 0
        for number in range(n + 1):
            if (number >> bits) & 1:
                ones += 1
        zeroes = (n + 1) - ones
        # The Hamming distance contributed by this bit is the number of 1s times the number of 0s
        total += ones * zeroes
    return total

