def is_majority(arr, n, x):
    # Find the index of the first occurrence of x in arr[]
    i = first_occurrence(arr, 0, n-1, x, n)

    # If the element is not present at all, return false
    if i == -1:
        return False

    # Check if the element is present more than n/2 times
    if ((i + n // 2) <= (n - 1)) and arr[i + n // 2] == x:
        return True
    else:
        return False

def first_occurrence(arr, low, high, x, n):
    if(high >= low):
        mid = (low + high) // 2

        # Check if x is present at mid
        if ((mid == 0 or x > arr[mid-1]) and arr[mid] == x):
            return mid
        elif(x > arr[mid]):
            return first_occurrence(arr, (mid + 1), high, x, n)
        else:
            return first_occurrence(arr, low, (mid -1), x, n)

    return -1

