def find_rotation_count(arr):
    low = 0
    high = len(arr) - 1

    # The array is not rotated if the first element is less than the last element.
    if arr[low] < arr[high]:
        return 0

    while low <= high:
        mid = (low + high) // 2
        # If the middle element is greater than its next element then mid+1 element is the smallest
        if arr[mid] > arr[(mid + 1) % len(arr)]:
            return (mid + 1) % len(arr)
        # If the middle element is lesser than its previous element then mid element is the smallest
        elif arr[mid] < arr[(mid - 1) % len(arr)]:
            return mid
        # If the elements at mid, high, and low are equal, we cannot decide the smallest
        # element's position, move closer to the unsorted part.
        elif arr[mid] <= arr[low]:
            high = mid - 1
        else:
            low = mid + 1

    return 0

