def get_maxgold(gold, m, n):
    # Create a table for storing intermediate results and initialize all cells to 0.
    gold_table = [[0 for i in range(n)] for j in range(m)]

    for col in range(n - 1, -1, -1):
        for row in range(m):
            # Right (->)
            if (col == n - 1):
                right = 0
            else:
                right = gold_table[row][col + 1]

            # Right up (/)
            if (row == 0 or col == n - 1):
                right_up = 0
            else:
                right_up = gold_table[row - 1][col + 1]

            # Right down (\)
            if (row == m - 1 or col == n - 1):
                right_down = 0
            else:
                right_down = gold_table[row + 1][col + 1]

            # Update gold_table
            gold_table[row][col] = gold[row][col] + max(right, right_up, right_down)

    # The max amount of gold collected will be the max value in the first column
    res = gold_table[0][0]
    for i in range(1, m):
        res = max(res, gold_table[i][0])

    return res

