def find_Max(arr, low, high):
    # If the array is not rotated at all
    if arr[high] > arr[low]:
        return arr[high]

    # If there is only one element left
    if high == low:
        return arr[low]

    # Find mid
    mid = low + (high - low) // 2 # This helps to avoid overflow for large low and high

    # Check if element (mid+1) is maximum element. Consider the cases like [3, 4, 5, 1, 2]
    if mid < high and arr[mid+1] < arr[mid]:
        return arr[mid]

    # Check if mid itself is minimum element
    if mid > low and arr[mid] < arr[mid - 1]:
        return arr[mid-1]

    # Decide whether we need to go to the left half or the right half
    if arr[high] > arr[mid]:
        return find_Max(arr, low, mid-1)
    return find_Max(arr, mid+1, high)

