def remove_similar_row(matrix):
    # Use a set to automatically remove duplicates since sets do not allow duplicate elements
    unique_rows = set()

    # Transform each row of the matrix (which is a list of tuples) into a tuple itself so it can be added to a set
    for row in matrix:
