def rgb_to_hsv(r, g, b):
    r_prime = r / 255.0
    g_prime = g / 255.0
    b_prime = b / 255.0
    c_max = max(r_prime, g_prime, b_prime)
    c_min = min(r_prime, g_prime, b_prime)
    delta = c_max - c_min

    # Hue calculation
    if delta == 0:
        h = 0
    elif c_max == r_prime:
        h = 60 * (((g_prime - b_prime) / delta) % 6)
    elif c_max == g_prime:
