def count_Pairs(arr, n):
    # Create a dictionary to store the frequency of each number
    element_freq = {}

    # Populate the dictionary with element counts
    for elem in arr:
        if elem in element_freq:
            element_freq[elem] += 1
        else:
            element_freq[elem] = 1

    # Count the pairs
    pairs_count = 0
    for key in element_freq:
        # The formula for counting pairs of each element
        count = element_freq[key]
        pairs_count += (count * (count - 1)) // 2

