def find_triplet_array(arr, n, sum_val):
    # First, sort the array
    arr.sort()

    # Iterate through the array
    for i in range(0, n-2):
        # Initialize two pointers
        left = i + 1
        right = n - 1

        while left < right:
            # Check if the triplet sum equals the target sum
            if arr[i] + arr[left] + arr[right] == sum_val:
                return (arr[i], arr[left], arr[right])

            # If sum is less than the target sum, move the left pointer to the right
            elif arr[i] + arr[left] + arr[right] < sum_val:
                left += 1

            # If sum is greater than the target sum, move the right pointer to the left
            else:
                right -= 1

    # Return None if no triplet with the given sum is found
    return None

