def coin_change(coins, m, n):
    # Create a table to store solutions for subproblems
    dp = [0] * (n + 1)

    # Base case: There is 1 way to make change for 0
    dp[0] = 1

    # Fill dp[] in a bottom-up manner
    for coin in coins:
        for x in range(coin, n + 1):
            dp[x] += dp[x - coin]

    return dp[n]

