def lcs(a, b, m, n):
    """
    Function to find the length of the Longest Common Subsequence (LCS) of `a` and `b`.
    `m` and `n` are the lengths of strings `a` and `b` respectively.
    """
    L = [[0] * (n+1) for i in range(m+1)]

    # Building the LCS table in bottom-up manner
    for i in range(m+1):
        for j in range(n+1):
            if i == 0 or j == 0:
                L[i][j] = 0
            elif a[i-1] == b[j-1]:
                L[i][j] = L[i-1][j-1] + 1
            else:
                L[i][j] = max(L[i-1][j], L[i][j-1])

    return L[m][n]

