def find_closest(arr1, arr2, arr3, n1, n2, n3):
    diff = float('inf')  # Initialize difference as infinite
    res_i, res_j, res_k = 0, 0, 0

    # Initialize current pointers for arr1, arr2, and arr3
    i, j, k = 0, 0, 0

    while i < n1 and j < n2 and k < n3:
        # Find minimum and maximum of current three elements
        minimum = min(arr1[i], min(arr2[j], arr3[k]))
        maximum = max(arr1[i], max(arr2[j], arr3[k]))

        # Update result if current difference is smaller than the smallest difference found so far
        if maximum - minimum < diff:
            res_i, res_j, res_k = i, j, k
            diff = maximum - minimum

        # We can't decrease the maximum element or increase the minimum element to get a better result,
        # so we try to increase the minimum element by moving ahead in the array containing the minimum.
        if diff == 0: break
        if arr1[i] == minimum:
            i += 1
        elif arr2[j] == minimum:
            j += 1
        else:
            k += 1

    return (arr1[res_i], arr2[res_j], arr3[res_k])

