def join_tuples(tuples_list):
    # Create a dictionary for aggregating tuples by their first element
    agg_dict = {}
    for tup in tuples_list:
        # Check if first element of tuple already exists as a key in dictionary
        if tup[0] in agg_dict:
            # Append the second element of the current tuple to the value of the existing key
            agg_dict[tup[0]].append(tup[1])
        else:
            # Create a new key with the first element and start a list with the second element
            agg_dict[tup[0]] = [tup[1]]

    # Create a list for storing the joined tuples
    result = []
    for key, value in agg_dict.items():
        # Form a new tuple with the key and all values aggregated in the corresponding list
        new_tup = (key,) + tuple(value)
        result.append(new_tup)

    return result

