def longest_common_subsequence(X, Y, m, n):
    # Create a 2D array (list of lists) to store the length of LCS. Adding 1 to take care of the base case (i.e., 0 length)
    L = [[0] * (n+1) for i in range(m+1)]

    # Building the LCS table in bottom-up fashion
    for i in range(m+1):
        for j in range(n+1):
            if i == 0 or j == 0:
                L[i][j] = 0
            elif X[i-1] == Y[j-1]:
                L[i][j] = L[i-1][j-1]+1
            else:
                L[i][j] = max(L[i-1][j], L[i][j-1])

    # L[m][n] contains the length of LCS of X[0..n-1] & Y[0..m-1]
    return L[m][n]

