def is_prime_factor_exponent_even(n):
    """Helper function to check if all prime factor exponents are even."""
    i = 2
    while i * i <= n:
        exponent = 0
        while n % i == 0:
            exponent += 1
            n //= i
        if exponent % 2 != 0:
            return False
        i += 1
    return True if n == 1 else False  # If n is not 1, it's a prime number itself with an exponent of 1.

