def first_missing_positive(nums):
    if not nums:
        return 1

    # Cleansing the list
    # Replace all non-positive numbers and numbers larger than n with a zero
    n = len(nums)
    for i in range(n):
        if nums[i] <= 0 or nums[i] > n:
            nums[i] = 0

    # Use the index as a hash to record the frequency of each number
    # by setting the number at index (value - 1) to a negative value
    for i in range(n):
        if nums[i] != 0:
            val = abs(nums[i])
            if nums[val - 1] > 0:
                nums[val - 1] *= -1  # Mark as found
            elif nums[val - 1] == 0:
                nums[val - 1] = -1 * (n + 1)  # Special case to mark found while keeping distinguishable

    # The first positive index + 1 indicates the missing minimum positive number
    for i in range(n):
        if nums[i] >= 0:
            return i + 1

    return n + 1

