def first(arr, x, n):
    low = 0
    high = n - 1
    res = -1  # Initial value to indicate if x is not found

    while low <= high:
        mid = (low + high) // 2
        if arr[mid] > x:
            high = mid - 1
        elif arr[mid] < x:
            low = mid + 1
        else:
            res = mid
            high = mid - 1  # Move left to find the first occurrence

